// ... a spolocnu cast full 
// 2005-07-27: upravené / nahradené _vlastna_cast_kresponz reazcom _spolocna_cast_kresponz kvôli posvätnım èítaniam, viï nišie 
// 2005-08-16: nahradená "_vlastna_cast_hymnus" novou funkciou "_spolocna_cast_hymnus"
// 2009-01-07: dorobené debug vıpisy
// 2009-09-18: nové parametre; Log("brat_antifony == %d\n", brat_antifony);
// 2010-09-10: pre posv. èítanie sa responz medzi psalmódiou a 1. èítaním berie len v prípade, e sa berie vlastné 1. èítanie
// 2011-08-12: doplnené pouitie pre modlitbu cez deò; modlitba sa pouije zo spoloènej èasti len pre slávnosti a sviatky (ináè sa MCD berie zo dòa, bod è. 236 všeobecnıch smerníc)
// 2011-08-13: zoh¾adnenie bodov 229, 232, 236
// 2013-07-04: pre doplnkovú psalmódiu treba prípadne ponastavova odlišné almy ( 129 resp.  131 namiesto  122, 126 resp. 127), pretoe almy sa pre slávenia s nišou prioritou nastavujú zo altára; doplnková psalmódia sa preto nastavuje prv, ako sa nastavilo je_alternativa_doplnkova_psalmodia_z...()
#define _spolocna_cast_full(modl) {\
	Log("_spolocna_cast_full(%s): zaèiatok...\n", nazov_modlitby(modl));\
	Log("_global_poradie_svaty = %d\n", _global_poradie_svaty);\
	Log("force = %d\n", force);\
	Log("_global_den.typslav = %d\n", _global_den.typslav);\
	for(short int i = 0; i < MAX_POCET_SVATY; i++){\
		Log("_global_svaty(%d).typslav = %d\n", i + 1, _global_svaty(i + 1).typslav);\
	}\
	if(!((modl == MODL_PREDPOLUDNIM) || (modl == MODL_NAPOLUDNIE) || (modl == MODL_POPOLUDNI))){\
		_spolocna_cast_hymnus(modl, _global_den.litobd);\
	}\
	if(su_antifony_vlastne(modl) || ((force & FORCE_BRAT_ANTIFONY) == FORCE_BRAT_ANTIFONY)){\
		Log("_spolocna_cast_antifony(%s)\n", nazov_modlitby(modl));\
		_spolocna_cast_antifony;\
	}\
	else if((modl == MODL_PREDPOLUDNIM) || (modl == MODL_NAPOLUDNIE) || (modl == MODL_POPOLUDNI)){\
		Log("_spolocna_cast_antifony(%s) - MCD...\n", nazov_modlitby(modl));\
		if(su_antifony_vlastne(modl) || ((force & FORCE_BRAT_ANTIFONY_MCD) == FORCE_BRAT_ANTIFONY_MCD)){\
			_spolocna_cast_antifony;\
		}\
	}\
	else {\
		Log("_spolocna_cast_antifony(%s) - NEBERÚ SA!\n", nazov_modlitby(modl));\
	}\
	if((modl == MODL_PREDPOLUDNIM) || (modl == MODL_NAPOLUDNIE) || (modl == MODL_POPOLUDNI)){\
		Log("idem skúsi zoh¾adni _set_zalmy_mcd_doplnkova_psalmodia_alternativy(%s)\n", nazov_modlitby(modl));\
		if((_global_opt[OPT_1_CASTI_MODLITBY] & BIT_OPT_1_MCD_ZALMY_INE) == BIT_OPT_1_MCD_ZALMY_INE){\
			_set_zalmy_mcd_doplnkova_psalmodia_alternativy(modlitba);\
		}\
		if(su_kcit_kresp_modlitba_mcd_vlastne(modl)){\
			_vlastna_cast_kcitanie;\
			_spolocna_cast_kresponz;\
		}\
	}\
	else{\
		if(((su_inv_hymnus_kcit_kresp_benmagn_prosby_vlastne(modl)) || ((force & FORCE_BRAT_KCIT_1CIT) == FORCE_BRAT_KCIT_1CIT)) && (modl != MODL_POSV_CITANIE)){\
			_vlastna_cast_kcitanie;\
		}\
		if(su_inv_hymnus_kcit_kresp_benmagn_prosby_vlastne(modl) || ((force & FORCE_BRAT_KRESP_PROSBY) == FORCE_BRAT_KRESP_PROSBY)){\
			if(modl != MODL_POSV_CITANIE){\
				_spolocna_cast_kresponz;\
			}\
			if((modl == MODL_RANNE_CHVALY) || (modl == MODL_VESPERY) || (modl == MODL_PRVE_VESPERY)){\
				_vlastna_cast_prosby;\
			}\
		}\
	}\
	if(((je_1cit_vlastne(modl)) || ((force & FORCE_BRAT_KCIT_1CIT) == FORCE_BRAT_KCIT_1CIT)) && (modl == MODL_POSV_CITANIE)){\
		_spolocna_cast_kresponz;\
		_spolocna_cast_1citanie;\
	}\
	if(modl == MODL_RANNE_CHVALY){\
		if(su_inv_hymnus_kcit_kresp_benmagn_prosby_vlastne(modl)){\
			_vlastna_cast_benediktus;\
		}\
	}\
	else if((modl == MODL_VESPERY) || (modl == MODL_PRVE_VESPERY)){\
		if(su_inv_hymnus_kcit_kresp_benmagn_prosby_vlastne(modl)){\
			_vlastna_cast_magnifikat;\
		}\
	}\
	else if(modl == MODL_POSV_CITANIE){\
		if(su_inv_hymnus_kcit_kresp_benmagn_prosby_vlastne(modl) || ((force & FORCE_BRAT_2CITANIE) == FORCE_BRAT_2CITANIE)){\
			_spolocna_cast_2citanie;\
		}\
	}\
	if((modl == MODL_PREDPOLUDNIM) || (modl == MODL_NAPOLUDNIE) || (modl == MODL_POPOLUDNI)){\
		if(su_kcit_kresp_modlitba_mcd_vlastne(modl)){\
			_vlastna_cast_modlitba;\
		}\
	}\
	else{\
		_vlastna_cast_modlitba;\
	}\
	Log("_spolocna_cast_full(%s): koniec.\n", nazov_modlitby(modl));\
}
