			_global_modl_cez_den_12.alternativy += ((_global_modl_cez_den_12.alternativy & bit) != bit) ? bit : 0;
			break;
		case MODL_CEZ_DEN_3:
			_global_modl_cez_den_3.alternativy += ((_global_modl_cez_den_3.alternativy & bit) != bit) ? bit : 0;
			break;
