// switch(litobd), case OBD_VIANOCNE_I -- begin -----------------------------------------------
		case OBD_VIANOCNE_I :// do slavnosti zjavenia pana
			Log("OBD_VIANOCNE_I\n");

			// tu v skutočnosti začína VIANOČNÉ OBDOBIE I.

