				_global_modl_cez_den_9.alternativy += ((_global_modl_cez_den_9.alternativy & bit) != bit)? bit : 0;
				break;
			case MODL_CEZ_DEN_12:
				_global_modl_cez_den_12.alternativy += ((_global_modl_cez_den_12.alternativy & bit) != bit)? bit : 0;
				break;
			case MODL_CEZ_DEN_3:
